<?php
namespace Gutenberg\ModalContent;

use Plugandplay\Pegasus\Core\BaseBlock;
use Timber\Timber;

if (!class_exists(ModalContent::class)) {
    /**
     * Class ModalContent
     * @author <Pegasus>
     * @package Gutenberg\ModalContent
     */
    class ModalContent extends BaseBlock
    {
        /**
        * Returns the acf config. Generated by Pegasus. Do not change this method.
        * @return string[]
        * @author <Pegasus>
        */
        static function getAcf(): array
        {
            $filesName = realpath(static::getPath() . '/../') . 'group_674ec916670f5.json';
            if (!file_exists($filesName)) {
                return [];
            }
            try {
                return json_decode(file_get_contents($filesName), true);
            } catch (\Exception $e) {
                return [];
            }
        }

        /**
        * @param array $block
        * @param array $fields
        * @param string $viewTemplate
        * @author <Pegasus>
        */
        public function __construct(array $block, array $fields = [])
        {
            // Custom dependency injection here
            parent::__construct($block, $fields);
        }

        /**
         * @return void
         * @author <Pegasus>
         */
        public function inflate(): void
        {
            if (empty($_GET['modal'])) {
                $this->addData(['backgroundColor' => '']);
            } else if (!empty($this->getData()['block']['backgroundColor'])) {
                $this->addData(['inModal' => true]);
                $this->addData(['backgroundColor' => $this->getData()['block']['backgroundColor']]);
            }
            // Put you custom application logic here, and load the data into $this->data.
            // You will reach the data in the template: {{ my_field }} or {{ this.getData().my_field }}
            // $this->addData(Timber::context());
            parent::inflate();
        }
    }
}
